window.onload=function(){const startTime=new Date("2024/5/12 00:00:00");function padZero(num){return num<10?"0"+num:String(num)}setInterval(()=>{const now=new Date,elapsedMilliseconds=now-startTime,totalSeconds=Math.floor(elapsedMilliseconds/1e3),days=Math.floor(totalSeconds/86400),hours=Math.floor(totalSeconds%86400/3600),minutes=padZero(Math.floor(totalSeconds%3600/60)),seconds=padZero(totalSeconds%60);document.getElementById("runtime").textContent=`花期：${days} 天 ${hours} 小时 ${minutes} 分 ${seconds} 秒`},1e3)};